mapper.select:
SELECT c.CODE_NAME ,b.BOARD_TYPE ,b.BOARD_NUM ,b.BOARD_TITLE ,b.BOARD_COMMENT ,b.TOTAL_CNT FROM ( SELECT BOARD_TYPE ,BOARD_NUM ,BOARD_TITLE ,BOARD_COMMENT ,ROW_NUMBER() OVER(ORDER BY BOARD_NUM DESC) AS NUMROW ,COUNT(*) OVER() AS TOTAL_CNT FROM BOARD
WHERE BOARD_TYPE IN (
'${item}'
)
) b JOIN COM_CODE c ON b.BOARD_TYPE = c.CODE_ID WHERE NUMROW BETWEEN (${pageNo}-1)*10 + 1 AND (${pageNo})*10

SELECT COUNT(*) AS TOTAL_CNT FROM BOARD WHERE BOARD_TYPE IN (
'${item}'
)

mapper.update:
    UPDATE BOARD 
    SET 
	     BOARD_TITLE = #{boardTitle}
	    ,BOARD_COMMENT = #{boardComment}
    WHERE 	BOARD_TYPE = #{boardType}
    AND 	BOARD_NUM  = #{boardNum}

mapper.delete:
	    DELETE FROM
	    	BOARD
	    WHERE 	BOARD_TYPE = #{boardType}
	    AND 	BOARD_NUM  = #{boardNum}
